contracts/payment_contract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Escrow {
    struct EscrowAgreement {
        address buyer;
        address seller;
        uint256 amount;
        address token; // Address of ERC20 token, or address(0) for ETH
        uint256 deadline;
        bool isCompleted;
    }

    mapping(uint256 => EscrowAgreement) public agreements;
    uint256 public agreementCount;

    event AgreementCreated(uint256 agreementId, address buyer, address seller, uint256 amount, address token, uint256 deadline);
    event FundsReleased(uint256 agreementId, address seller);
    event AutoReleased(uint256 agreementId);

    modifier onlyBuyer(uint256 agreementId) {
        require(msg.sender == agreements[agreementId].buyer, "Only buyer can perform this action");
        _;
    }

    modifier onlyActive(uint256 agreementId) {
        require(!agreements[agreementId].isCompleted, "Agreement is already completed");
        _;
    }

    function createAgreement(
        address _seller,
        uint256 _amount,
        address _token,
        uint256 _duration
    ) external payable returns (uint256) {
        require(_seller != address(0), "Seller address cannot be zero");
        require(_amount > 0, "Amount must be greater than zero");

        uint256 agreementId = agreementCount++;
        uint256 deadline = block.timestamp + _duration;

        agreements[agreementId] = EscrowAgreement({
            buyer: msg.sender,
            seller: _seller,
            amount: _amount,
            token: _token,
            deadline: deadline,
            isCompleted: false
        });

        if (_token == address(0)) {
            // ETH payment
            require(msg.value == _amount, "Incorrect ETH amount sent");
        } else {
            // ERC20 payment
            IERC20(_token).transferFrom(msg.sender, address(this), _amount);
        }

        emit AgreementCreated(agreementId, msg.sender, _seller, _amount, _token, deadline);
        return agreementId;
    }

    function releaseFunds(uint256 agreementId) external onlyBuyer(agreementId) onlyActive(agreementId) {
        EscrowAgreement storage agreement = agreements[agreementId];
        agreement.isCompleted = true;

        if (agreement.token == address(0)) {
            // Release ETH
            payable(agreement.seller).transfer(agreement.amount);
        } else {
            // Release ERC20 tokens
            IERC20(agreement.token).transfer(agreement.seller, agreement.amount);
        }

        emit FundsReleased(agreementId, agreement.seller);
    }

    function autoRelease(uint256 agreementId) external onlyActive(agreementId) {
        EscrowAgreement storage agreement = agreements[agreementId];
        require(block.timestamp >= agreement.deadline, "Deadline not reached");
        agreement.isCompleted = true;

        if (agreement.token == address(0)) {
            // Release ETH
            payable(agreement.seller).transfer(agreement.amount);
        } else {
            // Release ERC20 tokens
            IERC20(agreement.token).transfer(agreement.seller, agreement.amount);
        }

        emit AutoReleased(agreementId);
    }
}

